INSERT INTO ncaster_news VALUES (1, 2, 'Welcome to Ncaster 1.7', 'Ncaster 1.7 is the next installment of the ncaster Zeus series it incorporates many new an existing features and updates, from the improvements in article management, template management & field management to the all new exclusive features such as the article (Hub,Bind,Relate) systems, Custom Field Filtering, Entity2 & the unique template fall back system.<br><br>Some Features<br><br>- New multi pages engine.<br>- WYSIWYG html editor<br>- Field types & updated custom field engine.<br>- Associative Articles.<br>- Hubs relation system<br>- Build list 2, including fast multi category selection and many more options.<br>- Brand new highly customizable fallback template system.<br>- Entity 2 & Php template coding<br>- Blocks w/ Nuke support.<br>- Advanced Custom field filtering system<br>and much more.', 'ndreamer', 1072073879, '', 'news', 'Dec-2003', 1, 0, 0, 2, 1);
