INSERT INTO ncaster_templates VALUES (15, 'module:display{category:9};', '{* We are using the display module for this template to give a listing of the games information such as developer,publiser, players, score\\\'s and description this page also refers to pages with reviews, previews & news related to this game, this should be a decent example of the use of Relational articles. *} \r\n\r\n<?xml version=\\"1.0\\" encoding=\\"iso-8859-1\\"?>\r\n<!DOCTYPE html PUBLIC \\"-//W3C//DTD XHTML 1.0 Transitional//EN\\" \\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\\">\r\n<html xmlns=\\"http://www.w3.org/1999/xhtml\\">\r\n<head><title>Project ncaster: <!$myurl$></title>\r\n<meta http-equiv=\\"Content-Type\\" content=\\"text/html; charset=iso-8859-1\\" />\r\n<meta content=\\"ncaster, cms, ndream, freeware, wysiwug, xml, hubs, news publising\\" name=\\"Keywords\\" />\r\n<meta content=\\"ncaster is a very powerfull custimisable cms system.\\" \r\nname=\\"Description\\" />\r\n\r\n<meta content=\\"MSHTML 6.00.2600.0\\" name=\\"GENERATOR\\" />\r\n<link href=\\"ncasterimg/css/sheet.css\\" rel=\\"stylesheet\\" type=\\"text/css\\" />\r\n<link href=\\"ncasterimg/css/comments.css\\" rel=\\"stylesheet\\" type=\\"text/css\\">\r\n</head>\r\n<body>\r\n<table width=\\"85%\\" border=\\"0\\" align=\\"center\\" cellpadding=\\"0\\" cellspacing=\\"0\\">\r\n  <tbody>\r\n    <tr> \r\n      <td height=\\"88\\" colspan=\\"3\\"><a href=\\"http://ncaster.cjb.net/\\"><img src=\\"ncasterimg/casterlogo.gif\\" \r\n      alt=\\"ncaster logo\\" \r\n      name=\\"logo\\" width=\\"350\\" height=\\"95\\" border=\\"0\\" id=\\"logo\\" /></a> </td>\r\n    </tr>\r\n    <tr bgcolor=\\"#999999\\"> \r\n      <td colspan=\\"3\\"></td>\r\n    </tr>\r\n    <tr> \r\n      <td valign=\\"top\\" colspan=\\"3\\"> <div align=\\"right\\"><font face=\\"Verdana, Arial, Helvetica, sans-serif\\" \r\n      color=\\"aqua\\" size=\\"2\\"> \r\n          <p align=\\"right\\">| <a class=\\"menu2\\" href=\\"?\\">news</a>|<a href=\\"http://www.x-boxworld.net/ndream/forums/\\">communitiy</a>|<a class=\\"menu2\\" href=\\"#help\\">sites</a></p>\r\n          </font></div></td>\r\n    </tr>\r\n    <tr> \r\n      <td valign=\\"top\\" colspan=\\"3\\"> <div align=\\"left\\"></div></td>\r\n    </tr>\r\n    <tr> \r\n      <td valign=\\"top\\" width=\\"125\\"> <p class=\\"news\\">\r\n\r\n{* this is a compiled list entry it\\\'s basicly a pre-generated index, these are generated by the build list feature, in this case this entry is a headlines index *}\r\n\r\n{$var|compiled_list|\\"3\\"}\r\n\r\n</p></td>\r\n      <td valign=\\"top\\" width=\\"719\\" colspan=\\"2\\"> <p class=\\"title\\">\r\n\r\n{* Our right hub nav and hub information, this is faily straight forward we are just using basic tags to fill in the information, no entity code just yet. *}\r\n\r\n<table width=\\"100%\\" border=\\"0\\" cellspacing=\\"0\\" cellpadding=\\"0\\">\r\n  <tr> \r\n    <td width=\\"77%\\" valign=\\"top\\"><p align=\\"center\\"><font size=\\"3\\" face=\\"Verdana, Arial, Helvetica, sans-serif\\"><strong><!$subject$></strong></font></p>\r\n      <p align=\\"left\\">Author - <!$username$></p>\r\n      <p align=\\"left\\">Description:<br>\r\n        <!$description$></p>\r\n      <p>&nbsp;</p>\r\n      <p>&nbsp;</p>\r\n      <p>&nbsp;</p>\r\n      <p>&nbsp;</p>\r\n      <p>&nbsp;</p>\r\n      <p>&nbsp;</p></td>\r\n    <td width=\\"23%\\" valign=\\"top\\"> \r\n      <table width=\\"100%\\" border=\\"0\\" cellspacing=\\"0\\" cellpadding=\\"0\\">\r\n        <tr> \r\n          <td><div align=\\"center\\"><font size=\\"2\\" face=\\"Verdana, Arial, Helvetica, sans-serif\\">Info</font></div></td>\r\n        </tr>\r\n        <tr> \r\n          <td><font size=\\"2\\" face=\\"Verdana, Arial, Helvetica, sans-serif\\"><strong>Developer: \r\n            </strong><!$developer$></font></td>\r\n        </tr>\r\n        <tr>\r\n          <td><font size=\\"2\\" face=\\"Verdana, Arial, Helvetica, sans-serif\\"><strong>Publisher: \r\n      